import { readFile } from 'fs/promises';
export async function safeReadFile(path, encoding = 'utf8') {
    try {
        return await readFile(path, encoding);
    }
    catch (error) {
        return null;
    }
}
export function safeJSONParse(contents) {
    try {
        return JSON.parse(contents);
    }
    catch (error) {
        return null;
    }
}
export function safeJSONStringify(contents) {
    try {
        return JSON.stringify(contents);
    }
    catch (error) {
        return null;
    }
}
